pragma solidity >=0.6.0;

import "@openzeppelin/contracts/token/ERC20/SafeERC20.sol";

import "@openzeppelin/contracts/ownership/Ownable.sol";
import "./PolkaWar.sol";

contract MultiDistributeToken is Ownable {
    string public name = "PolkaWar: Distribute Token";

    using SafeERC20 for IERC20;
    PolkaWar polkaWar;

    constructor(PolkaWar _polkaWar) public {
        polkaWar = _polkaWar;
    }

    function distributeToken(
        address[] memory listUser,
        uint256[] memory listAmount
    ) public onlyOwner {
        for (uint256 i = 0; i < listUser.length; i++) {
            polkaWar.transfer(listUser[i], listAmount[i] * 1e18);
        }
    }

    function distributeToken(address[] memory listUser, uint256 amount)
        public
        onlyOwner
    {
        for (uint256 i = 0; i < listUser.length; i++) {
            polkaWar.transfer(listUser[i], amount * 1e18);
        }
    }

    function withdrawToken() public {
        polkaWar.transfer(owner(), polkaWar.balanceOf(address(this)));
    }

    receive() external payable {}
}
